import axios from 'axios'
import React from 'react'
import { useState } from 'react'
import '../App.css'
// import s from './userLogin.module.css'
//-----

function UserLogin() {

    let [usernameoremail, setUsernameoremail] = useState()
    let [username, setUsername] = useState()
    let [email, setEmail] = useState()
    let [password, setPassword] = useState()
    let [login, setLogin] = useState([])

    const mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

    //-----
    console.log(usernameoremail);
    console.log(password);
    //-----
    function handleSubmit(e){
        if(usernameoremail.match(mailformat)){
            setEmail(usernameoremail)
        }
        else{
            setUsername()
        }
        axios.get(`http://localhost:5000/login?username=${usernameoremail}&password=${password}`)
        .then((res)=>{
            setLogin(res.data)
        })
        .catch(()=>{
            console.log("Error fetching data ðŸ˜’");
        })

        e.preventDefault()
    }
    //-----
    return (
        <div className='loginBox'>
            <span className='heading'>User Login</span>
            <form action="" className='loginFormBox'>
                <input type="text" name='usernameoremail' placeholder='Enter Username/Email' 
                onChange={(e)=>{
                    setUsernameoremail(e.target.value)
                }} />
                <input type="password" name='password' placeholder='Enter Password' 
                onChange={(e)=>{
                    setPassword(e.target.value)
                }} />
                <input type="submit" id='ulbtn' value='Login' className='btn' onClick={handleSubmit} />
                <div className='msg'>
                </div>
            </form>
        </div>
    )
}

export default UserLogin
