import { useRef } from 'react';
import { useState } from 'react';
import './App.css';
import Login from './components/Login';
import Register from './components/Register'
function App() {
  let [isToggle, setIsToggle] = useState(true)
  let [btnClick,setBtnClick] = useState(true)

  let btn = useRef()
  return (
    <div className='app'>
      <div className='mainContent'>
        <span className='buttons'>
          <button ref={btn}
            onClick={() => {
              setIsToggle(true)
            }}>Register</button>

          <button ref={btn}
            onClick={() => {
              setIsToggle(false)
              {btnClick ?  btn.current.style = "background-color: var(--btn)": btn.current.style = "background-color: var(--subbg)"}
            }}>Login</button>
        </span>

        {isToggle ? <Register /> : <Login />}


      </div>
    </div>
  );
}

export default App;
