import axios from 'axios'
import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import s from './userhomepage.module.css'

function Userhomepage() {

  const [loggedIn, setLoggedIn] = useState(null);

useEffect(() => {
    const loggedUser = localStorage.getItem('user');
    setLoggedIn(Boolean(loggedUser));
  }, []);
  let {user} = useParams()
  let [userdata, setUserdata] = useState([])
  //-----
  // console.log(userdata);
  //-----
  useEffect(()=>{
    axios.get(`http://localhost:5000/getuserdata?username=${user}`)
    .then((res)=>{
        setUserdata(res.data)
        console.log("Data retreived !");
        console.log(userdata)
    })
    .catch(()=>{
      console.log("Error retreiving data !");
    })
  },[])
  //-----
  return (
    <div className={s.userhomepage}>
      <span className={s.heading}>User Login: {user}</span>
      <span className={s.subHeading}>Task List :</span>
      <table>
        <tr>
          <th>ID</th>
          <th>Title</th>
          <th>Description</th>
          <th>Start date</th>
          <th>End date</th>
          <th>Deadline date</th>
          <th>Status</th>
          <th>Action</th>
        </tr>
          {
            userdata.map((data)=>{
              return(
                <tr key={data[0]}>
                    <td>{data[0]}</td>
                    <td>{data[1]}</td>
                    <td>{data[2]}</td>
                    <td>{data[3]}</td>
                    <td>{data[4]}</td>
                    <td>{data[5]}</td>
                    <td>{data[6]}</td>
                    <td>{data[7]}</td>
                </tr>
              )
            })
          }
      </table>
    </div>
  )
}

export default Userhomepage
