import React from 'react'
import { useState } from 'react'
import AdminLogin from './AdminLogin'
import UserLogin from './UserLogin'
import s from './login.module.css'
import '../App.css'
import { useRef } from 'react'
import { useEffect } from 'react'
//-----

function Login() {

    let [isAdmin, setIsAdmin] = useState(true)
    let [isBtnClicked, setIsBtnClicked] = useState(false)
    let btn1 = useRef()
    let btn2 = useRef()
    //-----
    useEffect(()=>{
        btn1.current.style ="background-color: var(--btn)"; 
    },[])

    return (
        <div className='mainLoginBox'>
            <div className='buttons'>
                <button ref={btn1}
                    id={s.btn}
                    onClick={() => {
                        setIsAdmin(true)
                        setIsBtnClicked(true)
                        btn1.current.style ="background-color: var(--btn)"; 
                        btn2.current.style="background-color: var(--subbg)";
                    }}>User </button>

                <button ref={btn2}
                    id={s.btn}
                    onClick={() => {
                        setIsAdmin(false)
                        setIsBtnClicked(false)
                        btn2.current.style ="background-color: var(--btn)"; 
                        btn1.current.style="background-color: var(--subbg)";
                    }}>Admin</button>
            </div>

            <div className='login'>
                {isAdmin ? <UserLogin /> : <AdminLogin />}
            </div>

        </div>
    )
}

export default Login
