import axios from 'axios'
import React from 'react'
import { useState } from 'react'
import { useEffect } from 'react'
import { useParams, useNavigate } from 'react-router-dom'
import s from './userhomepage.module.css'

function Userhomepage() {

  let {user} = useParams()
  // const location = useLocation()
  let [userdata, setUserdata] = useState([])
  let [isLoggedIn, setIsLoggedIn] = useState(false)
  let nav = useNavigate()
  let statuslist = ["Todo", "In Progress", "Ready For Test", "Done"]
  let [newstatus, setNewstatus] = useState("")
  //-----
  // console.log(userdata);
  //-----
  console.log(newstatus);
  useEffect(()=>{
    // location.push(`/userhome/${user}`)
    axios.get(`http://localhost:5000/getuserdata?username=${user}`)
    .then((res)=>{
        setUserdata(res.data)
        console.log("Data retreived !");
        console.log(userdata)
        setIsLoggedIn(true)
        // {(isLoggedIn) ? Window.location.reload("/home") : Window.location.reload(`/userhome/${user}`)}
        
    })
    .catch(()=>{
      console.log("Error retreiving data !");
    })

    // window.addEventListener('popstate', (event) => {
    //   alert("Bye");
    //   setIsLoggedIn(false)
    // });

  },[])
function handleStatusChange(id){

  console.log(id);
  axios.post("`http://localhost:5000/updatestatus", {newstatus})
  .then(()=>{
    console.log("Status Updated ✔");
  })
  .catch(()=>{
    console.log("Error updating status !");
  })
}
  //-----
  return (
    <div className={s.userhomepage}>
      <span className={s.heading}>User Login: {user}</span>
      <span className={s.subHeading}>Task List :</span>
      <table>
        <tr>
          <th>ID</th>
          <th>Title</th>
          <th>Description</th>
          <th>Start date</th>
          <th>End date</th>
          <th>Deadline date</th>
          <th>Status</th>
          {/* <th>Action</th> */}
        </tr>
          {
            userdata.map((data)=>{
              return(
                <tr key={data[0]}>
                    <td>{data[0]}</td>
                    <td>{data[1]}</td>
                    <td>{data[2]}</td>
                    {/* <td>{data[3]}</td> */}
                    <td>{data[4] ? data[4]: '-'}</td>
                    <td>{data[5] ? data[5]: '-'}</td>
                    <td>{data[6] ? data[6]: '-'}</td>
                    <td>{<>
                    {data[7]} &nbsp;
                      <select name="selectstatus" defaultValue={data[7]} 
                      onChange={(e)=>{
                          setNewstatus(e.target.value)
                          console.log();
                          handleStatusChange(data[0])}}>
                        {
                            statuslist.map((data)=>{
                              return(
                                <option value="" key={data[0]}>{data}</option>
                              )
                            })
                        }
                        {console.log(data[0])}
                      </select>
                      </>
                    }</td>
                </tr>
              )
            })
          }
      </table>
    </div>
  )
}

export default Userhomepage
