import axios from 'axios'
import React from 'react'
import { useRef } from 'react'
import { useState } from 'react'
import '../App.css'
// import s from './userLogin.module.css'
//-----

function UserLogin() {

    let [usernameoremail, setUsernameoremail] = useState("")
    // let [username, setUsername] = useState("")
    // let [email, setEmail] = useState("")
    let [firstdata, setFirsdata] = useState("")
    let [password, setPassword] = useState()
    let [login, setLogin] = useState([])

    const mailformat = /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/;

    let msg = useRef()
    //-----
    console.log(usernameoremail);
    console.log(password);
    console.log(login);
    //-----
    function handleSubmit(e){
        if(usernameoremail === "" | password === ""){
            msg.current.style = "color: var(--errsmsg)"
            msg.current.innerHTML = "Fill all required field!"    
        }
        else if(usernameoremail !== ""){
            if(usernameoremail.match(mailformat)){
                setFirsdata(usernameoremail)
            }
            else{
                setFirsdata(usernameoremail)
            }
        }
        else{
            axios.get(`http://localhost:5000/login?username=${firstdata}`)
            .then((res)=>{
                setLogin(res.data)
                if(password != login){
                    msg.current.style = "color: var(--errsmsg)"
                    msg.current.innerHTML = "Username or Password is not correct!"
                }
                else{
                    msg.current.style = "color: var(--successsmsg)"
                    msg.current.innerHTML = "Login Successfull! ðŸ˜‰"
                }
            })
            .catch(()=>{
                console.log("Error fetching data ðŸ˜’");
            })
        }


        
        e.preventDefault()
    }
    //-----
    return (
        <div className='loginBox'>
            <span className='heading'>User Login</span>
            <form action="" className='loginFormBox'>
                <input type="text" name='usernameoremail' placeholder='Enter Username/Email' 
                onChange={(e)=>{
                    setUsernameoremail(e.target.value)
                }} />
                <input type="password" name='password' placeholder='Enter Password' 
                onChange={(e)=>{
                    setPassword(e.target.value)
                }} />
                <input type="submit" id='ulbtn' value='Login' className='btn' onClick={handleSubmit} />
                <div className='msg' ref={msg}>
                </div>
            </form>
        </div>
    )
}

export default UserLogin
