  }, 3000);

  e.preventDefault()
}
  return (
    <div className='register'>
      <span className='heading'>Enter the Details</span>
      <form action="" className='rLoginBox'>
        <input type="text" name='userName' placeholder='Enter Username'
          onChange={(e) => {
            setUsername(e.target.value)
          }} />
        <input type="email" name='email' placeholder='Enter Email'
          onChange={(e) => {
            setEmail(e.target.value)
          }} />
        <input type="text" name='firstName' placeholder='Enter First Name'
          onChange={(e) => {
            setFirstname(e.target.value)
          }} />
        <input type="text" name='lastName' placeholder='Enter Last Name'
          onChange={(e) => {
            setLastname(e.target.value)
          }} />
        <input type="password" name='password' placeholder='Password'
          onChange={(e) => {
            setPassword(e.target.value)
          }} />
        <input type="password" name='confirmPassword' placeholder='Confirm Password'
          onChange={(e) => {
            setCpassword(e.target.value)
          }} />
        <div className='selectUserType'>
          <span className='subHeading'>Admin: <input type="radio" name='selectAU' value='admin'
            onChange={(e) => {
              setUsertype(e.target.value)
            }} /></span>
          <span className='subHeading'>User: <input type="radio" name='selectAU' value='user'
            onChange={(e) => {
              setUsertype(e.target.value)
            }} /></span>
        </div>
        <input type="submit" id='regbtn' value='Register' className='btn' onClick={handleSubmit} />
        <div className='msg' ref={msg}>
          
        </div>
      </form>
    </div>
  )
}

export default Register
